import React, { useState , useEffect } from 'react';
import axios from 'axios';
import { createMessageRoute } from '../../utils/apiRoutes';
import {BiSend} from 'react-icons/bi';

function MessageInput({user, selected}) {

	const [message, setMessage] = useState('');

	const handleMessageChange = (e) => {
		setMessage(e.target.value)
	}

	const handleFormSubmit = async (e) => {
		const { data } = await axios.post(createMessageRoute, {
			body: message,
			sender: user._id,
			recipient: selected._id
		})
		console.log(data)
	}

	return (
		<div className='message-input'>
			<form action="/" method="get" onSubmit={(e) => handleFormSubmit(e)}>
				<input type="text" className='message-form' onChange={(e) => handleMessageChange(e)}/>
				<button type="submit" className='send-message'><BiSend /></button>
			</form>
		</div>
	)
}

export default MessageInput
