import React, { useState , useEffect } from 'react';
import axios from 'axios';
import { createMessageRoute } from '../../utils/apiRoutes';

function MessageInput({user, selected}) {

	const handleFormSubmit = async (e) => {
		const { data } = await axios.post(createMessageRoute, {
			body: "Hello",
			sender: user._id,
			recipient: selected._id
		})
	}

	console.log(selected)

	return (
		<div className='message-input'>
			<form action="/" method="get" onSubmit={e => handleFormSubmit(e)}>
			</form>
		</div>
	)
}

export default MessageInput
