import React, { useState } from 'react';

export default function Form () {

    const [message, setMessage] = useState({'subject' : '' , 'body' : '' , 'recipient' : ''});

    const handleChange = (type , e) => {
        let updatedValue = {type : e.target.value};
        setMessage((message) => ({
            ...message, ...updatedValue
        }));
    }

    const handleTextSend = (subject, body, recipient) => {
        console.log(subject, body, recipient)
    }


    return (
        <div>
            <form onSubmit={handleTextSend(message['subject'], message['body'], message['recipient'])}>

                <input name='subject' placeholder='Subject . . .' value={message['subject']} onChange={(e) => handleChange('subject',e)}/>
                <input name='body' placeholder='' value={message['body']} />
            </form>

        </div>
    )
}
